:root {
  font-size: 16px;
}
@-webkit-keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
.spin {
  animation: spin 2.5s linear infinite;
}
@-webkit-keyframes flash {
  0% {
    opacity: 0.2;
  }
  50% {
    opacity: 1.0;
  }
  100% {
    opacity: 0.2;
  }
}
@-webkit-keyframes flash-background-success {
  0% {
    background-color: rgba(200, 227, 170, 0);
    background-image: -webkit-linear-gradient(rgba(200, 227, 170, 0), rgba(200, 227, 170, 0.1));
  }
  20% {
    background-color: rgba(200, 227, 170, 0.3);
    background-image: -webkit-linear-gradient(rgba(200, 227, 170, 0.3), rgba(200, 227, 170, 0.1));
  }
  80% {
    background-color: rgba(200, 227, 170, 0.3);
    background-image: -webkit-linear-gradient(rgba(200, 227, 170, 0.3), rgba(200, 227, 170, 0.1));
  }
  100% {
    background-color: rgba(200, 227, 170, 0);
    background-image: -webkit-linear-gradient(rgba(200, 227, 170, 0), rgba(200, 227, 170, 0.1));
  }
}
@-webkit-keyframes flash-background-error {
  0% {
    background-color: rgba(251, 180, 175, 0);
    background-image: -webkit-linear-gradient(rgba(251, 180, 175, 0), rgba(251, 180, 175, 0.1));
  }
  20% {
    background-color: rgba(251, 180, 175, 0.3);
    background-image: -webkit-linear-gradient(rgba(251, 180, 175, 0.3), rgba(251, 180, 175, 0.1));
  }
  80% {
    background-color: rgba(251, 180, 175, 0.3);
    background-image: -webkit-linear-gradient(rgba(251, 180, 175, 0.3), rgba(251, 180, 175, 0.1));
  }
  100% {
    background-color: rgba(251, 180, 175, 0);
    background-image: -webkit-linear-gradient(rgba(251, 180, 175, 0), rgba(251, 180, 175, 0.1));
  }
}
.spin-flash {
  animation: flash 0.5s linear infinite, spin 2.5s linear infinite;
}
.build {
  min-width: 250px;
  font-family: Menlo, Consolas, 'DejaVu Sans Mono', monospace;
  font-weight: 600;
  font-size: 0.8rem;
}
.build .resizer {
  opacity: 0.0;
  position: absolute;
  border: solid 4px rgba(0, 0, 0, 0.1);
}
.build .heading {
  -webkit-user-select: none;
  line-height: 3.0em;
  padding: 0 3px;
}
.build .heading.success {
  animation: flash-background-success linear 1.2s;
}
.build .heading.error {
  animation: flash-background-error linear 1.2s;
}
.build .heading .heading-text {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}
.build .heading .control-container {
  float: right;
  margin-left: 0.5rem;
}
.build .heading .control-container .btn {
  color: #2196f3;
  margin: 0 1px;
}
.build .heading .control-container .title {
  float: left;
}
.build .heading .control-container .title .build-timer {
  margin-right: 0.5rem;
}
.build .heading .control-container .title.error {
  color: #f44336;
}
.build .heading .control-container .title.success {
  color: #8bc34a;
}
.build .output.override-theme {
  color: #fff;
  background-color: #000;
}
.build .output .terminal {
  padding: 0;
  margin: 0;
  height: 100%;
  width: 100%;
  font-weight: normal;
}
.build .output .terminal > div {
  white-space: nowrap;
  font-size: inherit;
}
.build .output .terminal-test {
  background-color: none;
  position: absolute;
  float: left;
  visibility: hidden;
  height: auto !important;
  width: auto !important;
  white-space: nowrap;
  border: none;
}
.opaque-hover {
  transition: opacity 0.2s linear;
  opacity: 0.6;
}
.opaque-hover:hover {
  opacity: 1.0;
}
#build-status-bar.status-success > a {
  color: #8bc34a;
}
#build-status-bar.status-error > a {
  color: #f44336;
}
