/*
 * This file will be loaded on package activation. You can directly stylizes
 * DOM elements by accessing selectors like `.workspace` or `.source`.
 *
 * For more information: https://atom.io/docs/latest/creating-a-package#stylesheets
 */
.atom-preview-container {
  overflow: scroll;
  box-sizing: border-box;
  padding: 0px;
}
.atom-preview-container code {
  padding: 0px;
  background: none;
}
.atom-preview-container atom-text-editor {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}
.atom-preview-container .preview-code-block {
  padding: 0px 9.5px;
}
.atom-preview-container .message {
  opacity: 0.8;
  position: absolute;
  right: 0;
  left: 0;
}
